import { useState } from "react"
import './App.css';


function App() {
  const [newTodo, setNewTodo] = useState('')
  const [allTodos, setAllTodos] = useState([])
  const [filteredTodos, setFilteredTodos] = useState(allTodos)
  const [currentFilter, setCurrentFilter] = useState('All')

  const handleInput = (e) => {
    setNewTodo(e.target.value)
  }

  const handleFilter = (filterName) => {
    setCurrentFilter(filterName)
    if (filterName == 'Active') {
      setFilteredTodos(allTodos.filter(todo => !todo.completed))
    } else if (filterName == 'Completed') {
      setFilteredTodos(allTodos.filter(todo => todo.completed))
    } else if (filterName == 'All') {
      setFilteredTodos(allTodos)
    }
  }

  const handleSubmit = (e) => {
    if (e.key == 'Enter') {
      setAllTodos(prevTodos => [...prevTodos, 
        {name: newTodo,
        completed: false}])
      handleFilter(currentFilter)
      setNewTodo('')
      }
  }


  const toggleCompleted = (toggleIdx) => {
    setAllTodos(prevTodos => prevTodos.map((todo, idx) => {
      if (toggleIdx == idx) {
        return {...todo, completed: !todo.completed}
      } else {
        return todo
      }
    }))
  }



  return (
    <div className="App">
      <header>
        <h1>todo</h1>
        <svg xmlns="http://www.w3.org/2000/svg" width="26" height="26"><path fill="#FFF" fillRule="evenodd" d="M13 21a1 1 0 011 1v3a1 1 0 11-2 0v-3a1 1 0 011-1zm-5.657-2.343a1 1 0 010 1.414l-2.121 2.121a1 1 0 01-1.414-1.414l2.12-2.121a1 1 0 011.415 0zm12.728 0l2.121 2.121a1 1 0 01-1.414 1.414l-2.121-2.12a1 1 0 011.414-1.415zM13 8a5 5 0 110 10 5 5 0 010-10zm12 4a1 1 0 110 2h-3a1 1 0 110-2h3zM4 12a1 1 0 110 2H1a1 1 0 110-2h3zm18.192-8.192a1 1 0 010 1.414l-2.12 2.121a1 1 0 01-1.415-1.414l2.121-2.121a1 1 0 011.414 0zm-16.97 0l2.121 2.12A1 1 0 015.93 7.344L3.808 5.222a1 1 0 011.414-1.414zM13 0a1 1 0 011 1v3a1 1 0 11-2 0V1a1 1 0 011-1z"/></svg>
      </header>
      <main>
        <div className="new-todo--container">
          <div className="new-todo--empty-circle empty-circle"></div>
          <input 
              onChange={handleInput}
              onKeyPress={handleSubmit}
              className="new-todo" 
              type="text"  
              placeholder="Create a new todo..." 
              value={newTodo} />
        </div>
        <ul className="todo-ul">
          {filteredTodos.map((todo, idx) => (
            <li 
            key={idx}
            onClick={() => toggleCompleted(idx)}
            className={"todo" + (todo.completed ? ' completed' : '')}>
              <div className="empty-circle"></div>
              <p>{todo.name}</p>
              <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18"><path fill="#494C6B" fillRule="evenodd" d="M16.97 0l.708.707L9.546 8.84l8.132 8.132-.707.707-8.132-8.132-8.132 8.132L0 16.97l8.132-8.132L0 .707.707 0 8.84 8.132 16.971 0z"/></svg>
            </li>
          ))}
        </ul>
        {allTodos.length > 0 && <div className="todo--footer">
          <p className="items-left">{allTodos.length} {`item${allTodos.length !== 1 ? 's' : ''} left`}</p>
          <p className="clear-completed" onClick={() => setAllTodos([])}>Clear Completed</p>
        </div>}
        <div className="filter-container">
          <p className={"filter" + (currentFilter == 'All' ? ' current-filter' : '')}
             onClick={(() => handleFilter('All'))}>
              All
          </p>
          <p className={"filter" + (currentFilter == 'Active' ? ' current-filter' : '')}
              onClick={(() => handleFilter('Active'))}>
                Active
          </p>
          <p className={"filter" + (currentFilter == 'Completed' ? ' current-filter' : '')}
             onClick={(() => handleFilter('Completed'))}>
            Completed
          </p>
        </div>
      </main>
    </div>

  );
}

export default App;
