import { useState, useEffect, useRef, Children } from "react"
import './App.css';

function App() {
  const [newTodo, setNewTodo] = useState('')
  const [id, setId] = useState(0)
  const [allTodos, setAllTodos] = useState([])
  const [filteredTodos, setFilteredTodos] = useState([])
  const [currentFilter, setCurrentFilter] = useState('All')

  useEffect(() => {
    setId(prevId => prevId + 1)
  }, [allTodos])

  const inputRef = useRef(null)
  useEffect(() => {
    if (inputRef) {
      inputRef.current.focus()
    }
  }, [inputRef])

  const renderedTodos = 
    currentFilter === 'Active' ? allTodos.filter(todo => !todo.completed) : 
    currentFilter === 'Completed' ? allTodos.filter(todo => todo.completed) :
    allTodos

  const handleInput = (e) => {
    setNewTodo(e.target.value)
  }

  const handleSubmit = (e) => {
    if (e.key == 'Enter') {
      setAllTodos(prevTodos => [
        {name: newTodo,
        completed: false,
        id: id}, 
        ...prevTodos
      ])
      setNewTodo('')
    }
  }

  const handleDelete = (id) => {
    setAllTodos(prevTodos => prevTodos.filter(todo => todo.id !== id))
  }

  const toggleCompleted = (toggledId) => {
    setAllTodos(prevTodos => prevTodos.map((todo) => {
      if (toggledId == todo.id) {
        return {...todo, completed: !todo.completed}
      } else {
        return todo
      }
    }))
  }




  /* drag and drop */

  function getDragAfterElement(y) {
    const draggableElements = [...document.querySelectorAll('.todo:not(.dragging)')]
    return draggableElements.reduce((closest, currentChild) => {
      const box = currentChild.getBoundingClientRect()
      // distance from mouse to middle of box
      const offset = y - box.top - box.height / 2
      if (offset < 0 && offset > closest.offset) {
        return { offset: offset, element: currentChild }
      } else {
        return closest
      }
    }, {offset: Number.NEGATIVE_INFINITY}.element)
  }

  useEffect(() => {
    const draggables = document.querySelectorAll('.todo')
    const container = document.querySelector('.todo-ul')
    draggables.forEach(draggable => {
      draggable.addEventListener('dragstart', () => {
        draggable.classList.add('dragging')
      })

      draggable.addEventListener('dragend', () => {
        draggable.classList.remove('dragging')
      })
    })

    container.addEventListener('dragover', e => {
      const afterElement = getDragAfterElement(e.clientY)
      console.log(afterElement);
      const draggable = document.querySelector('.dragging')
      container.appendChild(draggable)
    })
  }, [allTodos])



  return (
    <div className="App">
      <header>
        <h1>todo</h1>
        <svg xmlns="http://www.w3.org/2000/svg" width="26" height="26"><path fill="#FFF" fillRule="evenodd" d="M13 21a1 1 0 011 1v3a1 1 0 11-2 0v-3a1 1 0 011-1zm-5.657-2.343a1 1 0 010 1.414l-2.121 2.121a1 1 0 01-1.414-1.414l2.12-2.121a1 1 0 011.415 0zm12.728 0l2.121 2.121a1 1 0 01-1.414 1.414l-2.121-2.12a1 1 0 011.414-1.415zM13 8a5 5 0 110 10 5 5 0 010-10zm12 4a1 1 0 110 2h-3a1 1 0 110-2h3zM4 12a1 1 0 110 2H1a1 1 0 110-2h3zm18.192-8.192a1 1 0 010 1.414l-2.12 2.121a1 1 0 01-1.415-1.414l2.121-2.121a1 1 0 011.414 0zm-16.97 0l2.121 2.12A1 1 0 015.93 7.344L3.808 5.222a1 1 0 011.414-1.414zM13 0a1 1 0 011 1v3a1 1 0 11-2 0V1a1 1 0 011-1z"/></svg>
      </header>
      <main>
        <div className="new-todo--container">
          <div className="new-todo--empty-circle empty-circle"></div>
          <input 
              ref={inputRef}
              onChange={handleInput}
              onKeyPress={handleSubmit}
              className="new-todo" 
              type="text"  
              placeholder="Create a new todo..." 
              value={newTodo} />
        </div>
        <ul className="todo-ul">
          {renderedTodos.map((todo, idx) => (
            <li 
            key={idx}
            className={"todo" + (todo.completed ? ' completed' : '')}
            draggable={true}>
              <div onClick={() => toggleCompleted(todo.id)} className={
              "empty-circle " + (todo.completed ? "completed" : "")}>
                {todo.completed && <svg className="completed-check" xmlns="http://www.w3.org/2000/svg" width="11" height="9"><path fill="none" stroke="#FFF" strokeWidth="2" d="M1 4.304L3.696 7l6-6"/></svg>}
              </div>
              <p className="todo-name">{todo.name}</p>
              <svg onClick={() => handleDelete(todo.id)} className="close-icon" xmlns="http://www.w3.org/2000/svg" width="18" height="18"><path fill="#494C6B" fillRule="evenodd" d="M16.97 0l.708.707L9.546 8.84l8.132 8.132-.707.707-8.132-8.132-8.132 8.132L0 16.97l8.132-8.132L0 .707.707 0 8.84 8.132 16.971 0z"/></svg>
            </li>
          ))}
        </ul>
        {allTodos.length > 0 && <div className="todo--footer">
          <p className="items-left">
            {allTodos.filter(todo => !todo.completed).length + " " +
            `item${allTodos.filter(todo => !todo.completed).length !== 1 ? 's' : ''} left`}</p>
          <div className="desktop-filter-container filter-container">
            <p className={"filter" + (currentFilter == 'All' ? ' current-filter' : '')}
              onClick={(() => setCurrentFilter('All'))}>
                All
            </p>
            <p className={"filter" + (currentFilter == 'Active' ? ' current-filter' : '')}
                onClick={(() => setCurrentFilter('Active'))}>
                  Active
            </p>
            <p className={"filter" + (currentFilter == 'Completed' ? ' current-filter' : '')}
              onClick={(() => setCurrentFilter('Completed'))}>
              Completed
            </p>
          </div>
          <p className="clear-completed" onClick={() => setAllTodos([])}>Clear Completed</p>
        </div>}
        <div className="filter-container">
          <p className={"filter" + (currentFilter == 'All' ? ' current-filter' : '')}
             onClick={(() => setCurrentFilter('All'))}>
              All
          </p>
          <p className={"filter" + (currentFilter == 'Active' ? ' current-filter' : '')}
              onClick={(() => setCurrentFilter('Active'))}>
                Active
          </p>
          <p className={"filter" + (currentFilter == 'Completed' ? ' current-filter' : '')}
             onClick={(() => setCurrentFilter('Completed'))}>
            Completed
          </p>
        </div>
        {allTodos.length > 1 && <p className="drag-and-drop">Drag and drop to reorder list</p>}
      </main>
    </div>

  );
}

export default App;
